/* eslint-disable */
export default function getPageInfo(): DocPageProps {
    const lang = getLangControl();
    const vcsType = getVcsControl();
    const userSettings = getUserSettings();

    const router = {pathname: '/docs/overview/concepts/quotas-limits'};

    return ({
        ...getContent(lang),
        vcsType,
        lang,
        router,
        ...userSettings,
        headerHeight: userSettings.fullScreen ? 0 : 64,
        onChangeLang: (lang) => {
            __STORYBOOK_ADDONS.channel.emit('storybookjs/knobs/change', {
                name: 'Language',
                value: lang
            })
        },
        onChangeFullScreen: (value) => {
            __STORYBOOK_ADDONS.channel.emit('storybookjs/knobs/change', {
                name: 'Full screen',
                value: value ? 'true' : 'false'
            })
        },
        onChangeWideFormat: (value) => {
            __STORYBOOK_ADDONS.channel.emit('storybookjs/knobs/change', {
                name: 'Wide format',
                value: value ? 'true' : 'false'
            })
        },
        onChangeShowMiniToc: (value) => {
            __STORYBOOK_ADDONS.channel.emit('storybookjs/knobs/change', {
                name: 'Show mini toc',
                value: value ? 'true' : 'false'
            })
        },
        onChangeTheme: (value) => {
            __STORYBOOK_ADDONS.channel.emit('storybookjs/knobs/change', {
                name: 'Theme',
                value,
            })
        },
        onChangeTextSize: (value) => {
            __STORYBOOK_ADDONS.channel.emit('storybookjs/knobs/change', {
                name: 'Text size',
                value,
            })
        },
    });
}
